document.addEventListener("DOMContentLoaded",function(){function loadResource(lazyElement){if(lazyElement.tagName==="IMG"){lazyElement.src=lazyElement.dataset.src}else if(lazyElement.tagName==="VIDEO"){lazyElement.src=lazyElement.dataset.src}else{lazyElement.src=lazyElement.dataset.src}lazyElement.classList.remove("lazy")}const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){loadResource(entry.target);observer.unobserve(entry.target)}})});const lazyElements=document.querySelectorAll(".lazy");lazyElements.forEach(element=>observer.observe(element));window.addEventListener("load",function(){const loadTime=performance.timing.loadEventEnd-performance.timing.navigationStart;document.getElementById("load-time").textContent=loadTime});window.addEventListener("load",function(){const resources=performance.getEntriesByType("resource");let totalSize=0;resources.forEach(resource=>{if(resource.transferSize){totalSize+=resource.transferSize}});const totalSizeKB=(totalSize/1024).toFixed(2);document.getElementById("page-size").textContent=totalSizeKB})});